var request = require('request');
function Movie(bot) {
  bot.on('message', function (msg) {
    if (msg.text == 'movies') {
      var url = 'https://img.sparknotes.com/content/sparklife/sparktalk/papert0313_LargeWide.jpg';
      var photo = request(url);
      bot.sendPhoto(msg.chat.id, photo);
    }
  });
}
exports.Movie = Movie;